mapper.select:
SELECT

from course_section

    del=0

        AND course_id=#{courseId}


        and onsale=#{onsale}


ORDER BY parent_id , sort

SELECT

FROM course_section
WHERE course_id = #{courseId}
AND parent_id = #{parentId}
 AND sort > #{sort} 
ORDER BY sort ASC
LIMIT 0,1;

SELECT

FROM course_section
WHERE course_id = #{courseId}
AND parent_id = #{parentId}
 AND sort < #{sort} 
ORDER BY sort DESC
LIMIT 0,1;
